function BindTapHideEvents(i,n){i.bind("click",function(){n.is(":hidden")?(n.slideDown(500),i.find(".hint_pic_down").removeClass("hint_pic_down").addClass("hint_pic_up")):(n.slideUp(500),i.find(".hint_pic_up").addClass("hint_pic_down").removeClass("hint_pic_up"))})}function HideNav(){$("#OrganInfoPage .mycreate-list").hide()}$(function(){BindTapHideEvents($("#mycreate"),$(".mycreate-list")),HideNav()});